import{d as c,o as a,c as o,a as e}from"./index-0afa1053.js";const d={class:"markdown-body"},n=e(`<h2>Usage</h2><p>Cards are composed of different content blocks which are typically laid out in vertical succession. The card package provides the building blocks for composing your card components.</p><table><thead><tr><th>Component</th><th>Usage</th></tr></thead><tbody><tr><td><code class="">mcw-card</code></td><td>Mandatory, for the card component</td></tr><tr><td><code class="">mcw-card-media</code></td><td>Rich media container</td></tr><tr><td><code class="">mcw-card-primary-action</code></td><td>Typically the majority of the card</td></tr><tr><td><code class="">mcw-card-actions</code></td><td>Aarea is used to show different actions the user can take</td></tr><tr><td><code class="">mcw-card-action-buttons</code></td><td>Wraps buttons elements</td></tr><tr><td><code class="">mcw-card-action-icons</code></td><td>Wraps icons elements</td></tr></tbody></table><h3>Card</h3><pre><code class="language-html">&lt;mcw-card&gt;
  &lt;mcw-card-primary-action @click=&quot;onPrimaryAction&quot;&gt;
    &lt;mcw-card-media :src=&quot;card&quot; wide&gt;Media&lt;/mcw-card-media&gt;
  &lt;/mcw-card-primary-action&gt;
  &lt;section&gt;
    &lt;h2&gt;Title goes here&lt;/h2&gt;
    &lt;h4&gt;subtitle here&lt;/h4&gt;
  &lt;/section&gt;
  &lt;mcw-card-actions&gt;
    &lt;mcw-card-action-buttons&gt;
      &lt;mcw-button class=&quot;mdc-card__action mdc-card__action--button&quot; outlined
        &gt;ACTION&lt;/mcw-button
      &gt;
    &lt;/mcw-card-action-buttons&gt;
    &lt;mcw-card-action-icons&gt;
      &lt;mcw-material-icon
        class=&quot;mdc-card__action mdc-card__action--icon&quot;
        icon=&quot;star&quot;
      &gt;&lt;/mcw-material-icon&gt;
    &lt;/mcw-card-action-icons&gt;
  &lt;/mcw-card-actions&gt;
&lt;/mcw-card&gt;
</code></pre><table><thead><tr><th>props</th><th>Type</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code class="">outlined</code></td><td>Boolean</td><td></td><td>Removes the shadow and displays a hairline stroke instead</td></tr></tbody></table><h3>PrimaryAction</h3><p>This component is used as the container for primary tappable content.</p><blockquote><p>Supports Vue-Router props</p></blockquote><pre><code class="language-html">&lt;mcw-card&gt;
  &lt;mcw-card-primary-action&gt;
    &lt;p&gt;Content&lt;/p&gt;
  &lt;/mcw-card-primary-action&gt;
&lt;/mcw-card&gt;
</code></pre><h3>Media</h3><p>This area is used for showing rich media in cards.</p><pre><code class="language-html">&lt;mcw-card&gt;
  &lt;mcw-card-media :src=&quot;media.jpg&quot; wide /&gt;
&lt;/mcw-card&gt;
</code></pre><table><thead><tr><th>props</th><th>Type</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code class="">src</code></td><td>String</td><td>required</td><td>the backgound image source</td></tr><tr><td><code class="">square</code></td><td>Boolean</td><td></td><td>Automatically scales the media area’s height to equal its width</td></tr><tr><td><code class="">wide</code></td><td>Boolean</td><td></td><td>Automatically scales the media area’s height, maintaining a 16:9 aspect ratio.</td></tr></tbody></table><blockquote><p>by default the media area size is wide (16x9)</p></blockquote><h3>Actions</h3><p>Acts as a container for <code class="">&lt;mcw-card-action-buttons&gt;</code> and/or <code class="">&lt;mcw-card-action-icons&gt;</code>. It should be used as the last child of the <code class="">&lt;mcw-card&gt;</code> component.</p><pre><code class="language-html">&lt;mcw-card-actions&gt;
  &lt;mcw-card-action-buttons&gt;
    &lt;mcw-button outlined&gt;ACTION&lt;/mcw-button&gt;
  &lt;/mcw-card-action-buttons&gt;
  &lt;mcw-card-action-icons&gt;
    &lt;mcw-material-icon icon=&quot;star&quot;&gt;&lt;/mcw-material-icon&gt;
  &lt;/mcw-card-action-icons&gt;
&lt;/mcw-card-actions&gt;
</code></pre><table><thead><tr><th>props</th><th>Type</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code class="">full-bleed</code></td><td>Boolean</td><td></td><td>Removes the action area’s padding</td></tr></tbody></table><h3>Action Buttons and Icons</h3><p>To display buttons and icons in the same row, wrap them in <code class="">action-buttons</code> and <code class="">action-icons</code> elements:</p><pre><code class="language-html">&lt;mcw-card-actions&gt;
  &lt;mcw-card-action-buttons&gt;
    &lt;mcw-button outlined&gt;ACTION&lt;/mcw-button&gt;
  &lt;/mcw-card-action-buttons&gt;
  &lt;mcw-card-action-icons&gt;
    &lt;mcw-material-icon icon=&quot;star&quot;&gt;&lt;/mcw-material-icon&gt;
  &lt;/mcw-card-action-icons&gt;
&lt;/mcw-card-actions&gt;
</code></pre><h3>Reference</h3><ul><li><a href="https://github.com/material-components/material-components-web/tree/master/packages/mdc-card">https://github.com/material-components/material-components-web/tree/master/packages/mdc-card</a></li></ul>`,24),r=[n],h={},p="",g=c({__name:"README",setup(s,{expose:t}){return t({frontmatter:{},excerpt:void 0}),(i,l)=>(a(),o("div",d,r))}});export{g as default,p as excerpt,h as frontmatter};
